using PyCall
using JLD
@pyimport xarray as xr

function make_weights(nb,ngc,ngn,ngm,wt)
  mg = 16
  wtsm = zeros(mg)
  rwgt = zeros(nb*mg)
  
  igcsm = 0
  for ibnd = 1:nb
    iprsm = 0
    if (ngc[ibnd] < mg)
      for igc = 1:ngc[ibnd]
        igcsm += 1
        wtsum = 0.
        for ipr = 1:ngn[igcsm]
          iprsm += 1
          wtsum += wt[iprsm]
        end
        wtsm[igc] = wtsum
      end
      for ig = 1:mg
        ind = (ibnd-1)*mg + ig
        rwgt[ind] = wt[ig]/wtsm[ngm[ind]]
      end
    else
      for ig = 1:mg
        igcsm += 1
        ind = (ibnd-1)*mg + ig
        rwgt[ind] = 1.0
      end
    end
  end
  
  wtsm,rwgt
end

function fancy_sum(array,ibnd,ngn,rwgt,rwgt_on=true)
  println(size(array))
  mg = 16
  local_ngn = ngn[findfirst(cumsum(ngn),(ibnd-1)*mg)+1:findfirst(cumsum(ngn),ibnd*mg)]
  new_array = zeros(size(array)[1:end-1]...,length(local_ngn))
  dims = length(size(array))
  cumsum_local_ngn = cumsum(local_ngn)
  for i in 1:length(local_ngn)
    ngn_range = (cumsum_local_ngn[i]-local_ngn[i]+1):cumsum_local_ngn[i]
    local_rwgt = rwgt_on ? rwgt[(ibnd-1)*mg + ngn_range] : ones(length(ngn_range))
    input = sum(array[fill(:,dims-1)...,ngn_range].*reshape(local_rwgt,(fill(1,dims-1)...,local_ngn[i])),dims)
    new_array[fill(:,dims-1)...,i] = (dims > 1) ? input : input[1]
  end
  new_array
end

