      lw_dict["kao_mo3"] = permutedims(rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"O3"),:,:,:],(3,2,1))
      lw_dict["ccl4o"] = rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"CCL4"),:,1,1]

    end

    if iband in [13]
      lw_dict["kao_mco"] = permutedims(rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"CO"),:,:,:],(3,2,1))
      lw_dict["kbo_mo3"] = rrtmg_lw_dset["AbsorptionCoefficientsUpperAtmos"][:values][1,iband,findfirst(AbsorberNames,"O3"),:,:,1]'
    end

    if iband in [6,8]
      lw_dict["cfc12o"] = rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"CFC12"),:,1,1]
      lw_dict["kao_mco2"] = rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"CO2"),:,:,1]'
    elseif iband in [7,13]
      lw_dict["kao_mco2"] = permutedims(rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"CO2"),:,:,:],(3,2,1))
    end

    if iband in [7,8]
      lw_dict["kbo_mco2"] = rrtmg_lw_dset["AbsorptionCoefficientsUpperAtmos"][:values][1,iband,findfirst(AbsorberNames,"CO2"),:,:,1]'
    end

    if iband in [6]
      lw_dict["cfc11adjo"] = rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"CFC11"),:,1,1]
    end

    if iband in [11]
      lw_dict["kao_mo2"] = rrtmg_lw_dset["AbsorptionCoefficientsLowerAtmos"][:values][1,iband,findfirst(AbsorberNames,"O2"),:,:,1]'
      lw_dict["kbo_mo2"] = rrtmg_lw_dset["AbsorptionCoefficientsUpperAtmos"][:values][1,iband,findfirst(AbsorberNames,"O2"),:,:,1]'
    end

    lw_dict["selfrefo"] = rrtmg_lw_dset["H20SelfAbsorptionCoefficients"][:values][1,iband,:,:]'
    lw_dict["forrefo"] = rrtmg_lw_dset["H20ForeignAbsorptionCoefficients"][:values][1,iband,:,:]'

    fixed_keys = copy(collect(keys(lw_dict)))
    for key in fixed_keys
      println("$(iband) $(key)")
      value = lw_dict[key]
      new_key = "cfc12o" == key ? "cfc12" : join([replace(fragment,r"([^2c])o$",s"\1") for fragment in split(key,"_")],"_")
      lw_dict[new_key] = fancy_sum(value,iband,ngn,rwgt,!(new_key in ["fracrefa","fracrefb"]))
      
      if new_key in ["ka","kb"]
        lw_dict[replace(new_key,"k","abs")] = reshape(lw_dict[new_key],(prod(size(lw_dict[new_key])[1:(end-1)]),size(lw_dict[new_key])[end]))
      end

      delete!(lw_dict,key)
    end

    push!(lw_params,lw_dict)
  end
  
  ntbl = 10000
  tau_tbl = fill(1.e10,ntbl+1)
  exp_tbl = fill(expeps,ntbl+1)
  tfn_tbl = ones(ntbl+1)

  for itr = 1:(ntbl-1)
    tfn = itr / ntbl
    tau_tbl[itr+1] = bpade * tfn / (1. - tfn)
    exp_tbl[itr+1] = exp(-tau_tbl[itr+1])
    if (exp_tbl[itr+1] <= expeps)
      exp_tbl[itr+1] = expeps
    end
    if (tau_tbl[itr+1] < 0.06)
      tfn_tbl[itr+1] = tau_tbl[itr+1]/6.
    else
      tfn_tbl[itr+1] = 1.-2.*((1./tau_tbl[itr+1])-(exp_tbl[itr+1]/(1.-exp_tbl[itr+1])))
    end
  end
   
  save("jlds/LW_parameters.jld","lw_params",lw_params,"totplnk",totplnk,"chi_mls",chi_mls,"tau_tbl",tau_tbl,"exp_tbl",exp_tbl,"tfn_tbl",tfn_tbl)
  println("LW parameters written to jlds/LW_parameters.jld")
end

